import React from 'react';
import './Certifications.css';

const certifications = [
  {
    title: "React Developer",
    issuer: "Coursera",
    link: "https://example.com/certificate1",
    image: "/src/assets/cert1.jpg"
  },
  {
    title: "Video Editing Mastery",
    issuer: "Udemy",
    link: "https://example.com/certificate2",
    image: "/src/assets/cert2.jpg"
  },
  // Add more certifications as needed
];

function Certifications() {
  return (
    <section>
      <h1>Certifications</h1>
      <p>
        Continuously learning and improving skills through professional certifications and courses.
      </p>
      <div className="cert-grid">
        {certifications.map((cert, idx) => (
          <a href={cert.link} target="_blank" rel="noopener noreferrer" key={idx} className="cert-card">
            <img src={cert.image} alt={cert.title} />
            <div>
              <h2>{cert.title}</h2>
              <p>{cert.issuer}</p>
            </div>
          </a>
        ))}
      </div>
    </section>
  );
}

export default Certifications;
