import React from 'react';
import './Projects.css'; // <-- Correct import for the CSS file
import { motion } from 'framer-motion';

const projects = {
  web: [
    {
      title: "Portfolio Website",
      description: "A modern portfolio built with React.",
      link: "https://github.com/yourname/portfolio",
      image: "/src/assets/web1.jpg"
    },
    // Add more web projects
  ],
  video: [
    {
      title: "Travel Vlog Edit",
      description: "Dynamic video editing for YouTube.",
      link: "https://youtube.com/yourvideo",
      image: "/src/assets/video1.jpg"
    },
    // Add more video projects
  ],
  content: [
    {
      title: "Tech Blog",
      description: "Writing about web development trends.",
      link: "https://medium.com/@yourname",
      image: "/src/assets/content1.jpg"
    },
    // Add more content writing projects
  ]
};

function Projects() {
  return (
    <section className="projects-section">
      <h1>Projects</h1>
      <h2>Web Development</h2>
      <div className="project-grid">
        {projects.web.map((proj, idx) => (
          <motion.a
            href={proj.link}
            target="_blank"
            rel="noopener noreferrer"
            key={idx}
            className="project-card"
            whileHover={{ scale: 1.05, boxShadow: "0 0 32px #ffd700" }}
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.1, duration: 0.6, type: "spring" }}
          >
            <img src={proj.image} alt={proj.title} />
            <div>
              <h3>{proj.title}</h3>
              <p>{proj.description}</p>
            </div>
          </motion.a>
        ))}
      </div>
      <h2>Video Editing</h2>
      <div className="project-grid">
        {projects.video.map((proj, idx) => (
          <motion.a
            href={proj.link}
            target="_blank"
            rel="noopener noreferrer"
            key={idx}
            className="project-card"
            whileHover={{ scale: 1.05, boxShadow: "0 0 32px #ffd700" }}
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.1, duration: 0.6, type: "spring" }}
          >
            <img src={proj.image} alt={proj.title} />
            <div>
              <h3>{proj.title}</h3>
              <p>{proj.description}</p>
            </div>
          </motion.a>
        ))}
      </div>
      <h2>Content Writing</h2>
      <div className="project-grid">
        {projects.content.map((proj, idx) => (
          <motion.a
            href={proj.link}
            target="_blank"
            rel="noopener noreferrer"
            key={idx}
            className="project-card"
            whileHover={{ scale: 1.05, boxShadow: "0 0 32px #ffd700" }}
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.1, duration: 0.6, type: "spring" }}
          >
            <img src={proj.image} alt={proj.title} />
            <div>
              <h3>{proj.title}</h3>
              <p>{proj.description}</p>
            </div>
          </motion.a>
        ))}
      </div>
    </section>
  );
}

export default Projects;
