import React from 'react';
import { motion } from 'framer-motion';
import './Home.css';

function Home({ theme }) {
  return (
    <section className={`home-hero ${theme}`}>
      <motion.div
        className="hero-content"
        initial={{ opacity: 0, y: 60 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, type: "spring" }}
      >
        <motion.img
          src="/src/assets/your-photo.jpg"
          alt="Your Name"
          className="hero-photo"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.8, type: "spring" }}
          whileHover={{ scale: 1.08, boxShadow: "0 0 32px #ffd700" }}
        />
        <motion.h1
          initial={{ opacity: 0, x: -40 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.5, duration: 0.7 }}
        >
          Hello, I'm <span className="highlight">Your Name</span>
        </motion.h1>
        <motion.h2
          initial={{ opacity: 0, x: 40 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.7, duration: 0.7 }}
        >
          Web Developer | Video Editor | Content Writer
        </motion.h2>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1, duration: 0.7 }}
        >
          Welcome to my portfolio! Explore my work, certifications, and get in touch.
        </motion.p>
        <motion.a
          href="/projects"
          className="cta-btn"
          whileHover={{ scale: 1.08, boxShadow: "0 0 32px #ffd700" }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          View My Projects
        </motion.a>
      </motion.div>
    </section>
  );
}

export default Home;
