import React from 'react';
import './Home.css';
import Me from '../assets/Me.jpg';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin } from 'react-icons/fa';

const Home = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
        delayChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 40
      }
    }
  };

  const imageVariants = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 10,
        delay: 0.2
      }
    },
    hover: {
      scale: 1.05,
      transition: {
        type: "spring",
        stiffness: 400,
        damping: 10
      }
    }
  };

  const socialVariants = {
    hidden: { scale: 0, rotate: -180 },
    visible: {
      scale: 1,
      rotate: 0,
      transition: {
        type: "spring",
        stiffness: 200,
        damping: 20
      }
    },
    hover: {
      scale: 1.2,
      rotate: 360,
      transition: {
        type: "spring",
        stiffness: 400,
        damping: 10
      }
    }
  };

  return (
    <motion.section 
      className="home-section"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      <div className="home-content">
        <motion.div 
          className="text-content"
          variants={containerVariants}
        >
          <motion.h1 variants={itemVariants}>
            Hi, I'm <span className="highlight">Abhi Yadav</span>
          </motion.h1>
          <motion.h2 variants={itemVariants}>Full Stack Developer</motion.h2>
          <motion.p variants={itemVariants}>
            I build exceptional and accessible digital experiences for the web.
          </motion.p>
          <motion.div 
            className="social-links"
            variants={itemVariants}
          >
            <motion.a
              href="https://github.com/AbhiYadav2003"
              target="_blank"
              rel="noopener noreferrer"
              variants={socialVariants}
              whileHover="hover"
            >
              <FaGithub />
            </motion.a>
            <motion.a
              href="https://www.linkedin.com/in/abhi-yadav-2003/"
              target="_blank"
              rel="noopener noreferrer"
              variants={socialVariants}
              whileHover="hover"
            >
              <FaLinkedin />
            </motion.a>
          </motion.div>
        </motion.div>

        <motion.div 
          className="hero-photo-circle"
          variants={imageVariants}
          whileHover="hover"
        >
          <motion.img 
            src={Me} 
            alt="Abhi Yadav" 
            className="profile-image"
            whileHover={{ scale: 1.1 }}
          />
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Home;
